package com.lucky.spring.util;

/**
 * Created by zhangdd on 2020/11/21
 */
public class CreateMailContentUtil {

    static final String JIE_RU = "data:image/jpg;base64,";
    static final String YUN_YING = "data:image/jpg;base64,";

    /**
     * 获取忘记密码所发送的email内容
     *
     * @param emailPage
     * @param token
     * @return
     */
    public static String createForgetMailContent(String emailPage, String token, int type) {
        String tokenString = emailPage + "?code=" + token;
        StringBuffer buffer = new StringBuffer(""
                + " <div style=\"width: 500px; padding-left: 60px\">"
                + "     <img src=\"" + (type == 1 ? YUN_YING : JIE_RU) + "\" width=\"280px\" />"
                + "     <div>"
                + "         <p>您好:</p>"
                + "         <p>您正在学校大脑平台进行找回密码操作</p>"
                + "         <p>请通过点击以下按钮重置密码: </p>"
                + "         <div>"
                + "             <a target=\"_blank\" style=\"cursor:pointer; background: #3196fa; color: #fff!important; border: none; padding: 8px 10px; border-radius: 4px;font-size: 14px\" "
                + "                     href=\"" + tokenString + "\">重置密码</a>"
                + "         </div>"
                + "         <p>如果无法点击。 请复制以下链接到浏览器打开。</p>"
                + "         <a target=\"_blank\" style=\"text-decoration: none; color: #3196fa\" "
                + "                 href=\"" + tokenString + "\">" + tokenString + "</a>"
                + "         <hr />"
                + "         <p style=\"color: #666; font-size: 12px; text-align: center\">这是一封自动产生的邮件， 请勿回复</p>"
                + "     </div>"
                + " </div>"
        );
        return buffer.toString();
    }
}
